package com.proutechos.sandbox.radiolivealacarte.server.service.dataServices;import com.proutechos.sandbox.radiolivealacarte.server.service.dataServices.dataStorage.RadioLiveALaCarteDataStorage;import com.proutechos.utils.server.rest.config.exceptions.ProutechosBaseException;public class FeedbackService {    public void createFeedback(String programID, String feedback) throws ProutechosBaseException {        try {            RadioLiveALaCarteDataStorage.getInstance().createFeedback(programID, feedback);        } catch (ProutechosBaseException e) {            throw e;        }    }    public void deleteFeedback(String programID) throws ProutechosBaseException {        try {            RadioLiveALaCarteDataStorage.getInstance().deleteFeedback(programID);        } catch (ProutechosBaseException e) {            throw e;        }    }    public String getFeedback(String programID) throws ProutechosBaseException {        try {            return RadioLiveALaCarteDataStorage.getInstance().getFeedback(programID);        } catch (ProutechosBaseException e) {            throw e;        }    }    private static FeedbackService _instance = new FeedbackService();    public static FeedbackService getInstance() {        return _instance;    }}